import React, { useState, useEffect } from "react";
import axios from "axios";

const TODO = () => {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState("");
  const [editMode, setEditMode] = useState(null);

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/todo");
      setTodos(response.data);
    } catch (error) {
      console.error("Error fetching todos", error);
    }
  };

  const addTodo = async () => {
    try {
      const response = await axios.post("http://localhost:8000/api/todo", {
        title: newTodo,
        completed: false,
      });
      setTodos([...todos, response.data]);
      setNewTodo("");
    } catch (error) {
      console.error("Error adding todo", error);
    }
  };

  const deleteTodo = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/api/todo/${id}`);
      const updatedTodos = todos.filter((todo) => todo._id !== id);
      setTodos(updatedTodos);
    } catch (error) {
      console.error("Error deleting todo", error);
    }
  };

  const updateTodoStatus = async (id, completed) => {
    try {
      await axios.put(`http://localhost:8000/api/todo/${id}`, { completed });
      const updatedTodos = todos.map((todo) =>
        todo._id === id ? { ...todo, completed } : todo
      );
      setTodos(updatedTodos);
    } catch (error) {
      console.error("Error updating todo status", error);
    }
  };

  const toggleEditMode = (id) => {
    setEditMode(id);
  };

  const saveEditedTodo = async (id, updatedTitle) => {
    try {
      await axios.put(`http://localhost:8000/api/todo/${id}`, {
        title: updatedTitle,
      });
      const updatedTodos = todos.map((todo) =>
        todo._id === id ? { ...todo, title: updatedTitle } : todo
      );
      setTodos(updatedTodos);
      setEditMode(null); // Exit edit mode after saving
    } catch (error) {
      console.error("Error updating todo", error);
    }
  };

  return (
    <div>
      <h1>TODO List</h1>
      <input
        type="text"
        value={newTodo}
        onChange={(e) => setNewTodo(e.target.value)}
        placeholder="Enter new todo"
      />
      <button onClick={addTodo}>Add</button>

      <ul>
        {todos.map((todo) => (
          <li key={todo._id}>
            {editMode === todo._id ? (
              <>
                <input
                  type="text"
                  defaultValue={todo.title}
                  onChange={(e) => saveEditedTodo(todo._id, e.target.value)}
                />
                <button onClick={() => saveEditedTodo(todo._id, todo.title)}>
                  Save
                </button>
              </>
            ) : (
              <>
                <span
                  style={{
                    textDecoration: todo.completed ? "line-through" : "none",
                  }}
                >
                  {todo.title}
                </span>
                <button
                  onClick={() => updateTodoStatus(todo._id, !todo.completed)}
                >
                  {todo.completed ? "Mark Incomplete" : "Mark Complete"}
                </button>
                <button onClick={() => toggleEditMode(todo._id)}>Edit</button>
                <button onClick={() => deleteTodo(todo._id)}>Delete</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TODO;
